import React from 'react';
import PopupWithForm from './PopupWithForm.js';

function EditAvatarPopup(props) {



    // This but not. Use ref instead to pull from DOM
    const [avatar, setAvatar] = React.useState();



    function handleSubmit(e) {
        e.preventDefault();
        props.onUpdateAvatar({ avatar: avatar });
        props.onClose();
    }

    return (
        <PopupWithForm name="form-pic" title="Change userpic" isOpen={props.isOpen} btnText="Save" onClose={props.onClose}>
            <label className="form__input-field">
                <input id="form-name" name="primary" className="form__name form__input" type="url" placeholder="Image link" required />
                <span id="form-name-error" className="form__error"></span>
            </label>
        </PopupWithForm>
    );
}

export default EditAvatarPopup;
