import React from 'react';

function ImagePopup(props) {
    return (
        <div className={`picture ${props.card && (`popup-opened`)}`}>
            <div className="picture__column">
                <div className="picture__img" style={{ backgroundImage: `url(${props.card.link ? props.card.link : ''})` }} />
                <h2 className="picture__title">{props.card.name ? props.card.name : ''}</h2>
                <button type="button" aria-label="Close Picture Button" className="form__exit-button btn-animate"></button>
            </div>
        </div>
    );
}

export default ImagePopup;
