import React from 'react';
import { withRouter, Link } from 'react-router-dom';
import Header from './Header';

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: 'Email',
            password: 'Password'
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const {name, value} = e.target;
        this.setState({ [name]: value })
    }

    handleSubmit(e) {}

    render() {
        return (
            <>
                <Header />
                <main className="content">
                    <section className="register">
                        <h2 className="register__header">Sign Up</h2>
                        <input className="register__input" type="text" value={this.state.email} onChange={this.handleChange} required />
                        <input className="register__input" type="text" value={this.state.password} onChange={this.handleChange} required />
                        <button className="register__button btn-animate">Sign Up</button>
                        <Link to="/signin" className="register__link">Already a member? Log in here!</Link>
                    </section>
                </main>
            </>
        );
    }
}

export default withRouter(Register);
