import React from 'react';
import { withRouter } from 'react-router-dom';
import Header from './Header';

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: ''
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const {name, value} = e.target;
        this.setState({ [name]: value })
    }

    handleSubmit(e) {}

    render() {
        return (
            <>
                <Header />
                <main className="content">
                    <section className="register">
                        <h2 className="register__header">Sign Up</h2>
                        <input id="form-name" className="register__input" type="text" value={this.state.email} onChange={this.handleChange} required minLength="2" maxLength="40" pattern="[A-Za-z -]*" />
                        <input id="form-occupation" className="form__occupation form__input secondary" type="text" value={this.state.password} onChange={this.handleChange} required minLength="2" maxLength="200" />
                    </section>
                </main>
            </>
        );
    }
}

export default withRouter(Register);
