import React from 'react';
import PopupWithForm from './PopupWithForm.js';

function EditProfilePopup(props) {
    const [name, setName] = React.useState();
    const [description, setDescription] = React.useState();

    function handleName(e) {
        setName(e.target.value);
    }

    function handleDescription(e) {
        setDescription(e.target.value);
    }

    return (
        <PopupWithForm name="form-edit" title="Edit profile" isOpen={props.isOpen} btnText="Save" onClose={props.onClose}>
            <label className="form__input-field">
                <input id="form-name" name="primary" className="form__name form__input" type="text" placeholder="Name" value={name} onChange={handleName} required minLength="2" maxLength="40" pattern="[A-Za-z -]*" />
                <span id="form-name-error" className="form__error"></span>
            </label>
            <label className="form__input-field">
                <input id="form-occupation" name="secondary" className="form__occupation form__input secondary" type="text" placeholder="About" value={description} onChange={handleDescription} required minLength="2" maxLength="200" />
                <span id="form-occupation-error" className="form__error"></span>
            </label>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
