import React from 'react';

function PopupWithForm(props) {

    return(
        <form className="form" id="form-edit">
            <div className="form__container">
                <h2 className="form__title">{props.title}</h2>
                {props.children}
                <button type="submit" className="form__save-button btn-animate">{props.btnText}</button>
                <button type="button" aria-label="Close Form Button" className="form__exit-button btn-animate"></button>
            </div>
        </form>
    );
}

export default PopupWithForm;
