import React from 'react';
import { withRouter } from 'react-router-dom';
import Header from './Header';

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: ''
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    render() {
        return (
            <>
                <Header />
                <main className="content">
                    <section className="register">
                        <label className="form__input-field">
                            <input id="form-name" name="primary" className="form__name form__input" type="text" value={this.state.email} onChange={this.handleChange} required minLength="2" maxLength="40" pattern="[A-Za-z -]*" />
                            <span id="form-name-error" className="form__error"></span>
                        </label>
                        <label className="form__input-field">
                            <input id="form-occupation" name="secondary" className="form__occupation form__input secondary" type="text" value={this.state.password} onChange={this.handleChange} required minLength="2" maxLength="200" />
                            <span id="form-occupation-error" className="form__error"></span>
                        </label>
                    </section>
                </main>
            </>
        );
    }
}

export default withRouter(Register);
