import React from 'react';
import { withRouter } from 'react-router-dom';
import Header from './Header';

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: ''
        };
    }

    render() {
        return (
            <>
                <Header />
                <main className="content">
                    <form className="register">
                        <h2 className="register__header">Sign Up</h2>
                        <input name="email" className="register__input" type="text" value={this.state.email} onChange={this.handleChange} />
                        <input name="password" className="register__input" type="text" value={this.state.password} onChange={this.handleChange} required />
                        <button className="register__button btn-animate" onClick={this.handleSubmit}>Sign Up</button>
                        <Link to="/signin" className="register__link btn-animate">Already a member? Log in here!</Link>                        
                    </form>
                </main>
            </>
        );
    }
}

export default withRouter(Login);
